.read './db/elections/2009_european/voting_locations_2016.sql'

insert or ignore into election_periods(id, name) values (11, 'Избори за членове на европейския парламент 2009');
insert or ignore into elections (id, name, round1_date, election_period_id) values
(30, 'Избори за членове на европейския парламент 2009', '2009-06-07', 11);

drop table _2009_european_protocols;

insert or ignore into location_neighborhoods (location_id, neighborhood) select distinct location_id, replace(neighborhood, '"', '') from voting_locations_2016 where neighborhood != '';

insert or ignore into sections (election_period_id, location_id, section_code) select 11, (select id from locations where locations.ekatte=_2009_european_sections.ekatte), section_id from _2009_european_sections;
drop table _2009_european_sections;

drop table _2009_european_preferences;

insert or ignore into voting_locations (location_id, cik_address, address, lat, lng, postcode, location_neighborhood_id) select location_id, address, replace(formatted_address, '"', ''), lat, lng, replace(postcode, '"', ''), (select id from location_neighborhoods where location_neighborhoods.location_id=voting_locations_2016.location_id and neighborhood=replace(voting_locations_2016.neighborhood, '"', '')) from voting_locations_2016;

update sections set address_id=(select id from voting_locations join voting_locations_2016 on voting_locations_2016.address=voting_locations.cik_address and sections.section_code=voting_locations_2016.section_id) where election_period_id=11 and address_id is null;
drop table temp_protocols;
drop table temp_preferences;
vacuum;